import React, { Component } from "react";
import { SimpleGallery } from "./ComponentsStateless";
class Counter extends Component {
  state = { count: 0 };
  render() {
    return (
      <div>
        <h3>{this.state.count}</h3>
        <button
          onClick={() => {
            this.setState({ count: this.state.count - 1 });
          }}
        >
          -
        </button>
        <button
          onClick={() => {
            this.setState({ count: this.state.count + 1 });
          }}
        >
          +
        </button>
      </div>
    );
  }
}

export { Counter };
