import React, { Component } from "react";
import { SimpleCanvas, SimpleGallery } from "./ComponentsStateless";
class Counter extends Component {
  state = { count: 0 };
  render() {
    return (
      <div>
        <h3>{this.state.count}</h3>
        <button
          onClick={() => {
            this.setState({ count: this.state.count - 1 });
          }}
        >
          -
        </button>
        <button
          onClick={() => {
            this.setState({ count: this.state.count + 1 });
          }}
        >
          +
        </button>
      </div>
    );
  }
}
class Gallery extends Component {
  state = { images: ["https://i.ytimg.com/vi/MCn9lL94sxQ/maxresdefault.jpg"] };
  render() {
    return (
      <div>
        <SimpleGallery images={this.state.images}></SimpleGallery>
        <button
          onClick={() => {
            let src = prompt("src");
            const imagesNew = [...this.state.images];
            imagesNew.push(src);
            this.setState({ images: imagesNew });
          }}
        >
          Add{" "}
        </button>
      </div>
    );
  }
}
class Canvasv2 extends Component {
  state = {
    data: [
      ["red", "white", "gray", "orange"],
      ["green", "yellow", "gray", "purple"],
      ["red", "white", "yellow", "green"],
      ["red", "green", "gray", "purple"],
    ],
  };
  render() {
    return (
      <div className="simple-canvas ">
        {state.data.map((row, rowIndex) => (
          <div className="row">
            {row.map((color, colIndex) => (
              <div
                className="cell"
                style={{ backgroundColor: color }}
                onClick={() => {
                  props.onCellClick(rowIndex, colIndex, color);
                }}
              ></div>
            ))}
          </div>
        ))}
      </div>
    );
  }
}

export { Counter, Gallery, Canvasv2 };
